import React from "react";

export default function QuestionCard(props) {
  return (
    <div className="container bg-light p-3 mb-2 mt-2 rounded border border-success">
      <span className="badge text-bg-primary" style={boldText}>
        Question :{" "}
      </span>
      &nbsp;{props.quesprop.question[0]}
      <div className="container mb-2 mt-2">
        <div className="row">
          <div className="col border border-dark">
            <span class="badge rounded-pill text-bg-warning mb-2 mt-2">
              a.{" "}
            </span>
            &nbsp;{props.quesprop.question[1]}
          </div>
          <div className="col border border-dark">
            <span class="badge rounded-pill text-bg-warning">b. </span>
            &nbsp;{props.quesprop.question[2]}
          </div>
          <div className="col border border-dark">
            <span class="badge rounded-pill text-bg-warning">c. </span>
            &nbsp;{props.quesprop.question[3]}
          </div>
          <div className="col border border-dark">
            <span class="badge rounded-pill text-bg-warning">d. </span>
            &nbsp;{props.quesprop.question[4]}
          </div>
        </div>
      </div>
      <div className="container  mb-2 mt-2">
        <span className="badge rounded-pill text-bg-success">Answer : </span>
        &nbsp;{props.quesprop.question[5]}
      </div>
    </div>
  );
}
